-- Habilitar la extensión uuid-ossp
CREATE EXTENSION IF NOT EXISTS "uuid-ossp" SCHEMA public;

-- Establecer el esquema de búsqueda por defecto
SET search_path = public;

-- 1. Eliminar la tabla existente si ya existe y quieres recrearla desde cero
DROP TABLE IF EXISTS public.species CASCADE;

-- 2. Creación de la tabla de Especies (species)
CREATE TABLE public.species (
    id_specie uuid DEFAULT gen_random_uuid() PRIMARY KEY,
    scientific_name text UNIQUE NOT NULL,
    common_name text NOT NULL,
    type text NOT NULL,
    habitat text NOT NULL,
    size text,
    ecological_role text,
    conservation_status text,
    description text,
    distribution text,
    family text,
    created_at timestamp with time zone DEFAULT now(),
    updated_at timestamp with time zone DEFAULT now()
);

-- Crear índices para mejorar el rendimiento de búsqueda
CREATE INDEX idx_species_scientific_name ON public.species (scientific_name);
CREATE INDEX idx_species_common_name ON public.species (common_name);
CREATE INDEX idx_species_type ON public.species (type);
CREATE INDEX idx_species_habitat ON public.species (habitat);
CREATE INDEX idx_species_conservation_status ON public.species (conservation_status);



INSERT INTO public.species (id_specie, scientific_name, common_name, type, habitat, size, ecological_role, conservation_status, description, distribution, family) VALUES
(uuid_generate_v4(), 'Espeletia grandiflora', 'Frailejón común/de oso', 'Planta', 'Páramos andinos (principalmente zonas bajas y medias)', 'Hasta 5 metros de altura con rosetas foliares de 1-2 metros de diámetro.', 'Productor primario, regulador hídrico, hábitat para fauna, fijador de suelo.', 'Preocupación Menor (LC)', 'Especie emblemática de los páramos andinos, caracterizada por su tallo robusto y roseta de hojas vellosas que capturan el agua atmosférica.', 'Páramos de Colombia y Venezuela.', 'Asteraceae'),
(uuid_generate_v4(), 'Espeletia hartwegiana', 'Frailejón de Hartweg', 'Planta', 'Páramos andinos (principalmente zonas bajas y medias)', 'Hasta 3 metros de altura con una roseta de hojas grandes y pilosas.', 'Productor primario, regulador hídrico, hábitat para microfauna.', 'Preocupación Menor (LC)', 'Frailejón común en los Andes colombianos, de aspecto robusto y hojas recubiertas de tricomas que le dan una apariencia blanquecina.', 'Páramos de Colombia y Ecuador.', 'Asteraceae'),
(uuid_generate_v4(), 'Espeletia pycnophylla', 'Frailejón de hojas densas', 'Planta', 'Páramos andinos altos y superpáramos', 'Hasta 4 metros de altura con hojas muy densas y superpuestas en la roseta.', 'Productor primario, regulador hídrico, adaptado a condiciones extremas de altura.', 'Preocupación Menor (LC)', 'Especie de frailejón que se distingue por la compactación de sus hojas, lo que le permite resistir bajas temperaturas y vientos fuertes en alturas elevadas.', 'Páramos de Colombia y Ecuador.', 'Asteraceae'),
(uuid_generate_v4(), 'Espeletia argentea', 'Frailejón plateado', 'Planta', 'Páramos andinos (zonas medias y altas)', 'Hasta 2.5 metros de altura, con hojas cubiertas de densos pelos plateados.', 'Productor primario, captador de agua, hábitat para insectos.', 'Preocupación Menor (LC)', 'Nombrada por el color plateado de sus hojas, debido a una densa pubescencia que la protege del frío y la radiación solar intensa.', 'Páramos de Colombia.', 'Asteraceae'),
(uuid_generate_v4(), 'Espeletiopsis corymbosa', 'Frailejón falso', 'Planta', 'Páramos y subpáramos', 'Arbusto de hasta 3 metros de altura, con inflorescencias en corimbo.', 'Productor primario, proporciona alimento y refugio a la fauna.', 'Preocupación Menor (LC)', 'Género cercano a Espeletia, con especies que comparten características morfológicas similares a los frailejones, pero con diferencias en la estructura floral.', 'Páramos de Colombia y Venezuela.', 'Asteraceae'),
(uuid_generate_v4(), 'Coespeletia timotensis', '(Frailejón de Timotes)', 'Planta', 'Páramos de alta montaña', 'Hasta 3 metros de altura.', 'Productor primario, indicador de salud del ecosistema de páramo.', 'Casi Amenazada (NT)', 'Aunque más común en Venezuela, algunas poblaciones o híbridos pueden encontrarse en los páramos fronterizos de Colombia. Posee hojas grandes y pubescentes.', 'Páramos de Venezuela (posiblemente Colombia).', 'Asteraceae'),
(uuid_generate_v4(), 'Libanothamnus neriifolius', 'Tabacote morado', 'Planta', 'Páramos y subpáramos', 'Arbusto de hasta 3 metros de altura.', 'Productor primario, fuente de alimento para aves y mamíferos.', 'Preocupación Menor (LC)', 'Arbusto con hojas lanceoladas y flores vistosas que atraen polinizadores, común en el sotobosque de los páramos andinos.', 'Páramos de Colombia y Venezuela.', 'Asteraceae'),
